{
  "name": "mkt-model",
  "modelfile": "FROM llama3\nSYSTEM Você é um especialista em marketing digital."
}

{  
  "tasks": [
   {
  "agent": "calendar_agent",
  "description": "Criar um calendário de posts para o LinkedIn, Instagram e Facebook, começando em 24/10/2024 até 31/12/2024, com exatamente 3 postagens por semana, referentes ao tópico de gestão de frotas na mineração. O agente deve também analisar o público de cada rede social (LinkedIn, Instagram, e Facebook) e sugerir os melhores dias e horários para maximizar engajamento com base em dados de público-alvo, ajustando o calendário de postagens de acordo com isso.",
  "expected_output": "calendário completo com datas e horários ideais para cada postagem em cada rede social, com base na análise de público e engajamento",
  "agent_details": {
    "role": "Especialista em Calendário e Análise de Público",
    "goal": "Criar um calendário otimizado para cada rede social com base no comportamento do público e tendências de engajamento, focando no LinkedIn, Instagram, e Facebook",
    "backstory": "Experiente em planejamento de mídia, análise de dados de redes sociais e estratégias de conteúdo para maximizar engajamento."
  }
    },
    {
      "agent": "text_content_agent",
      "description": "Escrever textos para os posts do calendário referentes ao tópico de gestão de frotas na mineração",
      "expected_output": "textos para posts",
      "agent_details": {
        "role": "Criador de Textos referentes ao tópico de gestão de frotas na mineração",
        "goal": "Escrever textos engajadores para os conteúdos planejados",
        "backstory": "Escritor criativo com habilidades em captar a essência da mensagem e adaptá-la para o público-alvo."
      }
    },
    {
      "agent": "image_creator_agent",
      "description": "Criar imagens para acompanhar os posts referentes ao tópico de gestão de frotas na mineração",
      "expected_output": "imagens prontas para upload",
      "agent_details": {
        "role": "Designer Gráfico",
        "goal": "Criar imagens e visuais para acompanhar os textos dos posts referentes ao tópico de gestão de frotas na mineração",
        "backstory": "Designer gráfico com forte senso estético, especializado em criar visuais impactantes para redes sociais."
      }
    },
    {
      "agent": "video_scriptwriter_agent",
      "description": "Desenvolver roteiros para vídeos do YouTube referentes ao tópico de gestão de frotas na mineração",
      "expected_output": "roteiros detalhados para vídeos",
      "agent_details": {
        "role": "Roteirista de Vídeos",
        "goal": "Elaborar roteiros para vídeos que serão publicados no YouTube referentes ao tópico de gestão de frotas na mineração",
        "backstory": "Roteirista experiente, especializado em conteúdo educativo e promocional para plataformas de vídeo."
      }
    },
    {
      "agent": "data_analytics_agent",
      "description": "Fornecer análise de dados de posts relacionados à gestão de frotas na mineração",
      "expected_output": "Relatórios com dados de performance e sugestões de melhoria",
      "agent_details": {
        "role": "Analista de Dados de Mídia Social",
        "goal": "Analisar a performance dos posts e identificar tendências",
        "backstory": "Especialista em análise de dados focado em insights para melhorar as estratégias de mídia social."
      }
    },
    {
      "agent": "customer_engagement_agent",
      "description": "Interagir com os usuários para aumentar o engajamento nos posts sobre gestão de frotas na mineração",
      "expected_output": "Aumento de interações significativas com o público",
      "agent_details": {
        "role": "Gerente de Engajamento do Cliente",
        "goal": "Aumentar o engajamento por meio de interações personalizadas",
        "backstory": "Especialista em construir comunidades engajadas em torno de marcas."
      }
    },
    {
      "agent": "campaign_strategist",
      "description": "Desenvolver uma estratégia global para campanhas no Google Ads, incluindo definição de objetivos, orçamento e segmentação de público.",
      "expected_output": "plano de estratégia detalhado para campanhas no Google Ads",
      "agent_details": {
        "role": "Estrategista de Campanha de Google Ads",
        "goal": "Definir estratégias eficazes para maximizar o ROI nas campanhas de anúncios",
        "backstory": "Especialista em marketing digital, com um histórico comprovado em campanhas de PPC."
      }
    },
    {
      "agent": "ad_group_creator",
      "description": "Criar dois grupos de anúncios para a campanha no Google Ads, cada um contendo 5 palavras-chave focadas no tema de gestão de frotas na mineração.",
      "expected_output": "dois grupos de anúncios criados com palavras-chave relevantes",
      "agent_details": {
        "role": "Especialista em Criação de Grupos de Anúncios",
        "goal": "Configurar grupos de anúncios altamente relevantes para maximizar a relevância e o desempenho dos anúncios",
        "backstory": "Especialista em SEM com vasta experiência em seleção e gerenciamento de palavras-chave."
      }
    },
    {
      "agent": "ad_creator",
      "description": "Criar 4 anúncios, 2 para cada grupo de anúncios, seguindo as especificações do Google Ads. Cada anúncio terá 7 títulos e 4 descrições conforme as regras do Google Ads.",
      "expected_output": "4 anúncios prontos para lançamento, cada um com múltiplos títulos e descrições",
      "agent_details": {
        "role": "Criador de Anúncios",
        "goal": "Produzir anúncios persuasivos e otimizados para as normas do Google Ads",
        "backstory": "Criativo publicitário especializado em copywriting e compliance com as diretrizes do Google Ads."
      }
    },
    {
      "agent": "seo_agent",
      "description": "Otimizar textos e posts relacionados à gestão de frotas na mineração para SEO",
      "expected_output": "Conteúdos otimizados para SEO",
      "agent_details": {
        "role": "Especialista em SEO",
        "goal": "Otimizar os conteúdos para melhorar o ranqueamento em motores de busca",
        "backstory": "Especialista em SEO técnico, com vasta experiência em palavras-chave e links."
      }
    },
    {
      "agent": "paid_media_manager",
      "description": "Gerenciar campanhas pagas no Google Ads e Facebook Ads para promover conteúdo relacionado à gestão de frotas na mineração",
      "expected_output": "Campanhas pagas otimizadas e relatórios de desempenho",
      "agent_details": {
        "role": "Gerente de Mídia Paga",
        "goal": "Maximizar o retorno sobre o investimento nas campanhas pagas",
        "backstory": "Especialista em PPC e remarketing, focado em alta performance de campanhas."
      }
    },
    {
      "agent": "automation_agent",
      "description": "Automatizar a distribuição de campanhas de e-mail relacionadas à gestão de frotas na mineração",
      "expected_output": "Funis de e-mail marketing automatizados",
      "agent_details": {
        "role": "Especialista em Automação de Marketing",
        "goal": "Automatizar campanhas de e-mail e marketing",
        "backstory": "Especialista em ferramentas de automação, criando funis eficazes de marketing."
      }
    },
    {
      "agent": "market_research_agent",
      "description": "Coletar dados de mercado sobre a gestão de frotas na mineração para identificar tendências e oportunidades",
      "expected_output": "Relatório detalhado de pesquisa de mercado",
      "agent_details": {
        "role": "Pesquisador de Mercado",
        "goal": "Analisar tendências e concorrentes para informar estratégias de marketing",
        "backstory": "Especialista em pesquisa e análise de mercado, com foco em mineração."
      }
    },
    {
      "agent": "influencer_manager",
      "description": "Identificar influenciadores na área de mineração para parcerias estratégicas",
      "expected_output": "Lista de influenciadores e proposta de parceria",
      "agent_details": {
        "role": "Gerente de Relacionamento com Influenciadores",
        "goal": "Criar parcerias estratégicas com influenciadores na área de mineração",
        "backstory": "Especialista em marketing de influência e gestão de relacionamentos de longo prazo."
      }
    }
  ]
}

